INSERT INTO poems (title, author, dynasty, content, excerpt, category, image_url, likes, views) VALUES
-- 唐诗部分
('静夜思', '李白', '唐', '床前明月光，疑是地上霜。举头望明月，低头思故乡。', '床前明月光，疑是地上霜。举头望明月，低头思故乡。', '唐诗', 'https://ai-public.mastergo.com/ai/img_res/6cef988e86519aeee312b949c3c77f80.jpg', 1245, 5000),
('春晓', '孟浩然', '唐', '春眠不觉晓，处处闻啼鸟。夜来风雨声，花落知多少。', '春眠不觉晓，处处闻啼鸟。夜来风雨声，花落知多少。', '唐诗', 'https://ai-public.mastergo.com/ai/img_res/6cef988e86519aeee312b949c3c77f80.jpg', 876, 4200),
('登鹳雀楼', '王之涣', '唐', '白日依山尽，黄河入海流。欲穷千里目，更上一层楼。', '白日依山尽，黄河入海流。欲穷千里目，更上一层楼。', '唐诗', 'https://ai-public.mastergo.com/ai/img_res/6cef988e86519aeee312b949c3c77f80.jpg', 654, 3500),
('望庐山瀑布', '李白', '唐', '日照香炉生紫烟，遥看瀑布挂前川。飞流直下三千尺，疑是银河落九天。', '日照香炉生紫烟，遥看瀑布挂前川。飞流直下三千尺，疑是银河落九天。', '唐诗', 'https://ai-public.mastergo.com/ai/img_res/6cef988e86519aeee312b949c3c77f80.jpg', 432, 3000),
('相思', '王维', '唐', '红豆生南国，春来发几枝。愿君多采撷，此物最相思。', '红豆生南国，春来发几枝。愿君多采撷，此物最相思。', '唐诗', 'https://ai-public.mastergo.com/ai/img_res/6cef988e86519aeee312b949c3c77f80.jpg', 789, 3800),
('鹿柴', '王维', '唐', '空山不见人，但闻人语响。返景入深林，复照青苔上。', '空山不见人，但闻人语响。返景入深林，复照青苔上。', '唐诗', 'https://ai-public.mastergo.com/ai/img_res/6cef988e86519aeee312b949c3c77f80.jpg', 567, 3200),
('江雪', '柳宗元', '唐', '千山鸟飞绝，万径人踪灭。孤舟蓑笠翁，独钓寒江雪。', '千山鸟飞绝，万径人踪灭。孤舟蓑笠翁，独钓寒江雪。', '唐诗', 'https://ai-public.mastergo.com/ai/img_res/6cef988e86519aeee312b949c3c77f80.jpg', 654, 3400),
('悯农', '李绅', '唐', '锄禾日当午，汗滴禾下土。谁知盘中餐，粒粒皆辛苦。', '锄禾日当午，汗滴禾下土。谁知盘中餐，粒粒皆辛苦。', '唐诗', 'https://ai-public.mastergo.com/ai/img_res/6cef988e86519aeee312b949c3c77f80.jpg', 876, 4100),
('黄鹤楼送孟浩然之广陵', '李白', '唐', '故人西辞黄鹤楼，烟花三月下扬州。孤帆远影碧空尽，唯见长江天际流。', '故人西辞黄鹤楼，烟花三月下扬州。孤帆远影碧空尽，唯见长江天际流。', '唐诗', 'https://ai-public.mastergo.com/ai/img_res/6cef988e86519aeee312b949c3c77f80.jpg', 543, 2900),
('早发白帝城', '李白', '唐', '朝辞白帝彩云间，千里江陵一日还。两岸猿声啼不住，轻舟已过万重山。', '朝辞白帝彩云间，千里江陵一日还。两岸猿声啼不住，轻舟已过万重山。', '唐诗', 'https://ai-public.mastergo.com/ai/img_res/6cef988e86519aeee312b949c3c77f80.jpg', 432, 2700),

-- 宋词部分
('水调歌头', '苏轼', '宋', '明月几时有？把酒问青天。不知天上宫阙，今夕是何年。我欲乘风归去，又恐琼楼玉宇，高处不胜寒。起舞弄清影，何似在人间。', '明月几时有？把酒问青天。不知天上宫阙，今夕是何年。', '宋词', 'https://ai-public.mastergo.com/ai/img_res/366643f2817681373d9f22ee88d7d419.jpg', 987, 4500),
('念奴娇·赤壁怀古', '苏轼', '宋', '大江东去，浪淘尽，千古风流人物。故垒西边，人道是，三国周郎赤壁。乱石穿空，惊涛拍岸，卷起千堆雪。江山如画，一时多少豪杰。', '大江东去，浪淘尽，千古风流人物。故垒西边，人道是，三国周郎赤壁。', '宋词', 'https://ai-public.mastergo.com/ai/img_res/366643f2817681373d9f22ee88d7d419.jpg', 765, 3800),
('青玉案·元夕', '辛弃疾', '宋', '东风夜放花千树。更吹落、星如雨。宝马雕车香满路。凤箫声动，玉壶光转，一夜鱼龙舞。', '东风夜放花千树。更吹落、星如雨。宝马雕车香满路。', '宋词', 'https://ai-public.mastergo.com/ai/img_res/366643f2817681373d9f22ee88d7d419.jpg', 543, 3200),
('声声慢', '李清照', '宋', '寻寻觅觅，冷冷清清，凄凄惨惨戚戚。乍暖还寒时候，最难将息。三杯两盏淡酒，怎敌他、晚来风急？', '寻寻觅觅，冷冷清清，凄凄惨惨戚戚。乍暖还寒时候，最难将息。', '宋词', 'https://ai-public.mastergo.com/ai/img_res/366643f2817681373d9f22ee88d7d419.jpg', 321, 2800),
('雨霖铃', '柳永', '宋', '寒蝉凄切，对长亭晚，骤雨初歇。都门帐饮无绪，留恋处，兰舟催发。执手相看泪眼，竟无语凝噎。', '寒蝉凄切，对长亭晚，骤雨初歇。都门帐饮无绪，留恋处，兰舟催发。', '宋词', 'https://ai-public.mastergo.com/ai/img_res/366643f2817681373d9f22ee88d7d419.jpg', 456, 3100),
('满江红', '岳飞', '宋', '怒发冲冠，凭栏处、潇潇雨歇。抬望眼，仰天长啸，壮怀激烈。三十功名尘与土，八千里路云和月。', '怒发冲冠，凭栏处、潇潇雨歇。抬望眼，仰天长啸，壮怀激烈。', '宋词', 'https://ai-public.mastergo.com/ai/img_res/366643f2817681373d9f22ee88d7d419.jpg', 678, 3900),
('蝶恋花', '欧阳修', '宋', '庭院深深深几许，杨柳堆烟，帘幕无重数。玉勒雕鞍游冶处，楼高不见章台路。', '庭院深深深几许，杨柳堆烟，帘幕无重数。玉勒雕鞍游冶处，楼高不见章台路。', '宋词', 'https://ai-public.mastergo.com/ai/img_res/366643f2817681373d9f22ee88d7d419.jpg', 345, 2600),
('浣溪沙', '晏殊', '宋', '一曲新词酒一杯，去年天气旧亭台。夕阳西下几时回？无可奈何花落去，似曾相识燕归来。', '一曲新词酒一杯，去年天气旧亭台。夕阳西下几时回？无可奈何花落去，似曾相识燕归来。', '宋词', 'https://ai-public.mastergo.com/ai/img_res/366643f2817681373d9f22ee88d7d419.jpg', 234, 2300),
('鹧鸪天', '晏几道', '宋', '彩袖殷勤捧玉钟，当年拚却醉颜红。舞低杨柳楼心月，歌尽桃花扇底风。', '彩袖殷勤捧玉钟，当年拚却醉颜红。舞低杨柳楼心月，歌尽桃花扇底风。', '宋词', 'https://ai-public.mastergo.com/ai/img_res/366643f2817681373d9f22ee88d7d419.jpg', 189, 2100),
('虞美人', '李煜', '宋', '春花秋月何时了？往事知多少。小楼昨夜又东风，故国不堪回首月明中。', '春花秋月何时了？往事知多少。小楼昨夜又东风，故国不堪回首月明中。', '宋词', 'https://ai-public.mastergo.com/ai/img_res/366643f2817681373d9f22ee88d7d419.jpg', 276, 2400),

-- 元曲部分
('天净沙·秋思', '马致远', '元', '枯藤老树昏鸦，小桥流水人家，古道西风瘦马。夕阳西下，断肠人在天涯。', '枯藤老树昏鸦，小桥流水人家，古道西风瘦马。夕阳西下，断肠人在天涯。', '元曲', 'https://ai-public.mastergo.com/ai/img_res/366643f2817681373d9f22ee88d7d419.jpg', 432, 2900),
('山坡羊·潼关怀古', '张养浩', '元', '峰峦如聚，波涛如怒，山河表里潼关路。望西都，意踌躇。伤心秦汉经行处，宫阙万间都做了土。', '峰峦如聚，波涛如怒，山河表里潼关路。望西都，意踌躇。伤心秦汉经行处，宫阙万间都做了土。', '元曲', 'https://ai-public.mastergo.com/ai/img_res/366643f2817681373d9f22ee88d7d419.jpg', 321, 2500),
('沉醉东风', '关汉卿', '元', '咫尺的天南地北，霎时间月缺花飞。手执着饯行杯，眼阁着别离泪。刚道得声保重将息，痛煞煞教人舍不得。', '咫尺的天南地北，霎时间月缺花飞。手执着饯行杯，眼阁着别离泪。', '元曲', 'https://ai-public.mastergo.com/ai/img_res/366643f2817681373d9f22ee88d7d419.jpg', 198, 1900),

-- 更多经典诗词
('将进酒', '李白', '唐', '君不见黄河之水天上来，奔流到海不复回。君不见高堂明镜悲白发，朝如青丝暮成雪。人生得意须尽欢，莫使金樽空对月。', '君不见黄河之水天上来，奔流到海不复回。君不见高堂明镜悲白发，朝如青丝暮成雪。', '唐诗', 'https://ai-public.mastergo.com/ai/img_res/6cef988e86519aeee312b949c3c77f80.jpg', 876, 4200),
('长恨歌', '白居易', '唐', '汉皇重色思倾国，御宇多年求不得。杨家有女初长成，养在深闺人未识。天生丽质难自弃，一朝选在君王侧。', '汉皇重色思倾国，御宇多年求不得。杨家有女初长成，养在深闺人未识。', '唐诗', 'https://ai-public.mastergo.com/ai/img_res/6cef988e86519aeee312b949c3c77f80.jpg', 654, 3800),
('琵琶行', '白居易', '唐', '浔阳江头夜送客，枫叶荻花秋瑟瑟。主人下马客在船，举酒欲饮无管弦。醉不成欢惨将别，别时茫茫江浸月。', '浔阳江头夜送客，枫叶荻花秋瑟瑟。主人下马客在船，举酒欲饮无管弦。', '唐诗', 'https://ai-public.mastergo.com/ai/img_res/6cef988e86519aeee312b949c3c77f80.jpg', 543, 3500),
('春江花月夜', '张若虚', '唐', '春江潮水连海平，海上明月共潮生。滟滟随波千万里，何处春江无月明！江流宛转绕芳甸，月照花林皆似霰。', '春江潮水连海平，海上明月共潮生。滟滟随波千万里，何处春江无月明！', '唐诗', 'https://ai-public.mastergo.com/ai/img_res/6cef988e86519aeee312b949c3c77f80.jpg', 765, 4100),
('锦瑟', '李商隐', '唐', '锦瑟无端五十弦，一弦一柱思华年。庄生晓梦迷蝴蝶，望帝春心托杜鹃。沧海月明珠有泪，蓝田日暖玉生烟。', '锦瑟无端五十弦，一弦一柱思华年。庄生晓梦迷蝴蝶，望帝春心托杜鹃。', '唐诗', 'https://ai-public.mastergo.com/ai/img_res/6cef988e86519aeee312b949c3c77f80.jpg', 432, 3200),
('无题', '李商隐', '唐', '相见时难别亦难，东风无力百花残。春蚕到死丝方尽，蜡炬成灰泪始干。晓镜但愁云鬓改，夜吟应觉月光寒。', '相见时难别亦难，东风无力百花残。春蚕到死丝方尽，蜡炬成灰泪始干。', '唐诗', 'https://ai-public.mastergo.com/ai/img_res/6cef988e86519aeee312b949c3c77f80.jpg', 543, 3400);

